// Получаем входные данные
let items = $input.all();
let Data = items[0].json.data;

// Устанавливаем максимальный размер для каждой части (измените при необходимости)
const MAX_SIZE = 5000;

// Инициализируем массив для хранения частей
let Data_Array = [];

// Регулярное выражение для поиска закрывающих тегов
const closingTagRegex = /<\/(p|div|section|article|ul|ol|li|h[1-6])>/gi;

// Находим все позиции закрывающих тегов
let match;
let splitPoints = [];
while ((match = closingTagRegex.exec(Data)) !== null) {
    splitPoints.push(match.index + match[0].length);
}

// Если не найдено ни одной точки разделения, разбиваем данные на части фиксированного размера
if (splitPoints.length === 0) {
    for (let start = 0; start < Data.length; start += MAX_SIZE) {
        Data_Array.push(Data.substring(start, start + MAX_SIZE));
    }
} else {
    // Инициализируем переменные для отслеживания текущей позиции
    let start = 0;
    let end = 0;

    for (let i = 0; i < splitPoints.length; i++) {
        let splitPoint = splitPoints[i];
        let chunkLength = splitPoint - start;

        if (chunkLength <= MAX_SIZE) {
            end = splitPoint;
        } else {
            if (end > start) {
                // Добавляем часть от start до end
                Data_Array.push(Data.substring(start, end));
                start = end;
                i--; // Повторно оцениваем текущую точку разделения
            } else {
                // Если нет подходящей точки разделения, разделяем на части фиксированного размера
                Data_Array.push(Data.substring(start, start + MAX_SIZE));
                start += MAX_SIZE;
                i--; // Повторно оцениваем текущую точку разделения
            }
        }
    }

    // Добавляем оставшиеся данные после последней точки разделения
    if (start < Data.length) {
        while ((Data.length - start) > MAX_SIZE) {
            Data_Array.push(Data.substring(start, start + MAX_SIZE));
            start += MAX_SIZE;
        }
        // Добавляем последнюю часть
        if (start < Data.length) {
            Data_Array.push(Data.substring(start));
        }
    }
}

// Преобразуем Data_Array в массив объектов для n8n
let output = Data_Array.map(part => {
    return { json: { data: part } };
});

// Возвращаем массив объектов
return output;

// Получаем входные данные
let items = $input.all();

// Инициализируем массив для хранения контента
let Data_Array = [];

// Проходим по каждому элементу и извлекаем контент
for (let item of items) {
    if (item.json && item.json.message && item.json.message.content) {
        Data_Array.push(item.json.message.content);
    } else {
        throw new Error('Элемент не содержит ожидаемого контента.');
    }
}

// Проверяем, что Data_Array является массивом
if (!Array.isArray(Data_Array)) {
    throw new Error('Data_Array должен быть массивом.');
}

// Объединяем все элементы массива в одну строку
let Data = Data_Array.join('');

// Возвращаем объединенные данные
return [{ json: { Data } }];
