// Получаем данные из узла Webhook1
const webhookData = $node["Webhook1"].json;

// Получаем данные из узла HTTP Request
const httpRequestData = $node["HTTP Request"].json;

// Функция для безопасного получения значения или установки 'None'
const getValue = (value) => (typeof value === 'string' ? value.trim() : (value !== undefined && value !== null ? value : "None"));

// Функция для генерации уникального ordercode
const generateOrderCode = () => `OC-${Date.now()}-${Math.floor(Math.random() * 1000).toString().padStart(3, '0')}`;

// Функция для определения языка формы на основе URL
const determineFormLang = (url) => {
  // Проверяем, начинается ли URL с нужного домена
  if (!url.startsWith("https://launey-a378t.wstd.io")) {
    return "None";
  }

  // Проверяем, является ли URL корневым
  if (/^https:\/\/launey-a378t\.wstd\.io\/?$/.test(url)) {
    return "RU";
  }

  try {
    const parsedUrl = new URL(url);
    const path = parsedUrl.pathname.toLowerCase();

    // Извлекаем первую часть пути после домена
    const firstSegment = path.split('/').filter(segment => segment.length > 0)[0] || "";

    switch (firstSegment) {
      case 'ru':
        return "RU";
      case 'ua':
        return "UA";
      case 'en':
        return "EN";
      default:
        return "None";
    }
  } catch (error) {
    return "None";
  }
};

// Получение URL из данных формы
const sourceUrl = getValue(webhookData.body["url"]);

// Определение customorder_lead_form_lang
const formLang = determineFormLang(sourceUrl);

// Определение customorder_source_add_info
const sourceAddInfo = (/^https:\/\/launey-a378t\.wstd\.io\/?$/.test(sourceUrl)) ? "None" : sourceUrl;

// Формируем итоговый JSON
const formattedData = {
  headers: getValue(webhookData.headers),
  params: getValue(webhookData.params),
  query: getValue(webhookData.query),
  body: {
    ordercode: generateOrderCode(),
    clientnamefirst: getValue(webhookData.body["First-Name"]),
    clientnamelast: getValue(webhookData.body["Last-Name"]),
    clientemail: getValue(webhookData.body["Email"]),
    clientphone: getValue(webhookData.body["Phone"]),
    customorder_source_url: sourceUrl,
    customorder_ga_code: "None",
    customorder_lead_IP: getValue(webhookData.body["ip"]),
    customorder_source_add_info: sourceAddInfo,
    customorder_lead_country: getValue(httpRequestData.country_code),
    customorder_lead_form_date: new Date().toISOString().split("T")[0], // Формат YYYY-MM-DD
    customorder_lead_region: getValue(httpRequestData.region),
    customorder_lead_city: getValue(httpRequestData.city),
    customorder_lead_location: `Latitude: ${getValue(httpRequestData.latitude)}, Longitude: ${getValue(httpRequestData.longitude)}`,
    customorder_lead_timezone: getValue(httpRequestData.timezone),
    customorder_lead_provider: getValue(httpRequestData.org),
    customorder_lead_hostname: getValue(httpRequestData.country_calling_code),
    customorder_utm_source: getValue(webhookData.body["utm_source"]),
    customorder_utm_medium: getValue(webhookData.body["utm_medium"]),
    customorder_utm_campaign: getValue(webhookData.body["utm_campaign"]),
    customorder_utm_content: getValue(webhookData.body["utm_content"]),
    customorder_utm_term: getValue(webhookData.body["utm_term"]),
    customorder_form_name: getValue(webhookData.body["First-Name"]),
    customorder_form_lastname: getValue(webhookData.body["Last-Name"]),
    customorder_lead_form_lang: formLang,
    customorder_form_message: getValue(webhookData.body["Message"]),
    customorder_brand_interest: "Navi.training",
    customorder_product_interest: getValue(webhookData.body["product"])
  },
  webhookUrl: getValue(webhookData.webhookUrl),
  executionMode: getValue(webhookData.executionMode)
};

// Возвращаем сформированный объект
return [
  {
    json: formattedData
  }
];
